import api, { setAuthToken } from './api';

export const getTransactions = async (params = {}) => {
  console.log('transactionService.js: Sending request with headers:', api.defaults.headers.common);
  const response = await api.get('/transactions', { params });
  return response.data;
};

export const createTransaction = async (transaction) => {
  console.log('transactionService.js: Sending request with headers:', api.defaults.headers.common);
  const response = await api.post('/transactions', transaction);
  return response.data;
};

export const uploadReceipt = async (file) => {
  console.log('transactionService.js: Uploading receipt with file:', file);
  const formData = new FormData();
  formData.append('file', file);
  const response = await api.post('/upload/receipt', formData, {
    headers: { 'Content-Type': 'multipart/form-data' },
  });
  return response.data;
};
