import { useState, useEffect } from 'react';
import { getTransactions } from '../services/transactionService';

function Dashboard() {
  const [transactions, setTransactions] = useState([]);
  const [summary, setSummary] = useState([]);

  useEffect(() => {
    const fetchData = async () => {
      const data = await getTransactions({ page: 1 });
      setTransactions(data.transactions);
      setSummary(data.summary || []);
    };
    fetchData();
  }, []);

  // Prepare data for Expenses by Category (Bar Chart)
  const expensesByCategory = transactions
    .filter(t => t.type === 'expense')
    .reduce((acc, t) => {
      acc[t.category] = (acc[t.category] || 0) + t.amount;
      return acc;
    }, {});
  const categoryLabels = Object.keys(expensesByCategory);
  const categoryData = Object.values(expensesByCategory);

  // Prepare data for Expenses by Date (Line Chart)
  const expensesByDate = transactions
    .filter(t => t.type === 'expense')
    .reduce((acc, t) => {
      const date = new Date(t.date).toLocaleDateString();
      acc[date] = (acc[date] || 0) + t.amount;
      return acc;
    }, {});
  const dateLabels = Object.keys(expensesByDate).sort();
  const dateData = Object.values(expensesByDate);

  const openCategoryChart = () => {
    const chartConfig = {
      "type": "bar",
      "data": {
        "labels": categoryLabels,
        "datasets": [{
          "label": "Expenses",
          "data": categoryData,
          "backgroundColor": ["#FF6384", "#36A2EB", "#FFCE56", "#4BC0C0", "#9966FF"],
          "borderColor": ["#FF6384", "#36A2EB", "#FFCE56", "#4BC0C0", "#9966FF"],
          "borderWidth": 1
        }]
      },
      "options": {
        "scales": {
          "y": {
            "beginAtZero": true,
            "title": {
              "display": true,
              "text": "Amount ($)"
            }
          }
        },
        "plugins": {
          "legend": {
            "position": "top"
          },
          "title": {
            "display": true,
            "text": "Expenses by Category"
          }
        }
      }
    };
    // Open canvas panel with chartConfig (assumed to be handled by the UI)
    window.openCanvasPanel('chart', chartConfig);
  };

  const openDateChart = () => {
    const chartConfig = {
      "type": "line",
      "data": {
        "labels": dateLabels,
        "datasets": [{
          "label": "Expenses",
          "data": dateData,
          "fill": false,
          "borderColor": "#FF6384",
          "tension": 0.1
        }]
      },
      "options": {
        "scales": {
          "y": {
            "beginAtZero": true,
            "title": {
              "display": true,
              "text": "Amount ($)"
            }
          },
          "x": {
            "title": {
              "display": true,
              "text": "Date"
            }
          }
        },
        "plugins": {
          "legend": {
            "position": "top"
          },
          "title": {
            "display": true,
            "text": "Expenses by Date"
          }
        }
      }
    };
    // Open canvas panel with chartConfig (assumed to be handled by the UI)
    window.openCanvasPanel('chart', chartConfig);
  };

  return (
    <div className="card">
      <h2 className="form-title">Dashboard</h2>
      <table>
        <thead>
          <tr>
            <th>Date</th>
            <th>Type</th>
            <th>Amount</th>
            <th>Category</th>
            <th>Description</th>
          </tr>
        </thead>
        <tbody>
          {transactions.map(t => (
            <tr key={t._id}>
              <td>{new Date(t.date).toLocaleDateString()}</td>
              <td>{t.type.charAt(0).toUpperCase() + t.type.slice(1)}</td>
              <td>{t.type === 'expense' ? `-${t.amount}` : t.amount}</td>
              <td>{t.category}</td>
              <td>{t.description}</td>
            </tr>
          ))}
        </tbody>
      </table>
      <h3>Monthly Summary</h3>
      {summary.length > 0 ? (
        summary.map(s => (
          <div key={s._id} className="summary-item">
            <p>
              Month: {s._id}, Income: ${s.totalIncome.toFixed(2)}, Expenses: ${s.totalExpenses.toFixed(2)}, Savings: ${s.savings.toFixed(2)}
            </p>
          </div>
        ))
      ) : (
        <p>No summary data available.</p>
      )}
      <h3>Charts</h3>
      <button onClick={openCategoryChart}>Show Expenses by Category</button>
      <button onClick={openDateChart}>Show Expenses by Date</button>
    </div>
  );
}

export default Dashboard;
