import { useState, useContext } from 'react';
import { createTransaction, uploadReceipt } from '../services/transactionService';
import { AuthContext } from '../context/AuthContext';

function TransactionForm({ onAdd }) {
  const [formData, setFormData] = useState({
    amount: '',
    date: new Date('2025-07-05T20:16:00+05:30').toISOString().split('T')[0],
    category: 'Food',
    description: '',
    type: 'expense',
    file: null,
  });
  const [error, setError] = useState('');
  const { token } = useContext(AuthContext);

  const handleSubmit = async (e) => {
    e.preventDefault();
    if (!formData.amount || isNaN(formData.amount)) {
      setError('Please enter a valid amount.');
      return;
    }
    try {
      console.log('TransactionForm: Submitting with token:', token?.substring(0, 10) + '...', 'FormData state:', formData);
      let transactionData = {
        ...formData,
        amount: parseFloat(formData.amount),
      };
      if (formData.file) {
        const formDataToSend = new FormData();
        formDataToSend.append('file', formData.file);
        console.log('TransactionForm: Uploading file:', formData.file.name, 'FormData entries:', [...formDataToSend.entries()]);
        console.log('TransactionForm: Full FormData object:', Object.fromEntries(formDataToSend)); // Additional debug
        const uploadResponse = await uploadReceipt(formDataToSend);
        transactionData = { ...transactionData, ...uploadResponse };
      }
      const transaction = await createTransaction(transactionData);
      onAdd(transaction);
      setFormData({ ...formData, amount: '', description: '', file: null });
      setError('');
    } catch (err) {
      console.error('TransactionForm: Error during submission:', err.response?.data?.message || err.message);
      setError(err.response?.data?.message || 'Error adding transaction');
    }
  };

  return (
    <div className="card">
      <h2 className="form-title">Add Transaction</h2>
      <form onSubmit={handleSubmit} encType="multipart/form-data">
        <div className="form-group">
          <label className="form-label">Type</label>
          <select
            value={formData.type}
            onChange={(e) => setFormData({ ...formData, type: e.target.value })}
            className="form-input"
          >
            <option value="expense">Expense</option>
            <option value="income">Income</option>
          </select>
        </div>
        <div className="form-group">
          <label className="form-label">Amount ($)</label>
          <input
            type="number"
            value={formData.amount}
            onChange={(e) => setFormData({ ...formData, amount: e.target.value })}
            className="form-input"
            required
            placeholder="Enter amount"
          />
        </div>
        <div className="form-group">
          <label className="form-label">Date</label>
          <input
            type="date"
            value={formData.date}
            onChange={(e) => setFormData({ ...formData, date: e.target.value })}
            className="form-input"
            required
          />
        </div>
        <div className="form-group">
          <label className="form-label">Category</label>
          <select
            value={formData.category}
            onChange={(e) => setFormData({ ...formData, category: e.target.value })}
            className="form-input"
          >
            <option value="Food">Food</option>
            <option value="Transport">Transport</option>
            <option value="Entertainment">Entertainment</option>
            <option value="Salary">Salary</option>
            <option value="Other">Other</option>
          </select>
        </div>
        <div className="form-group">
          <label className="form-label">Description</label>
          <input
            type="text"
            value={formData.description}
            onChange={(e) => setFormData({ ...formData, description: e.target.value })}
            className="form-input"
            placeholder="Enter description"
          />
        </div>
        <div className="form-group">
          <label className="form-label">Receipt (Image/PDF)</label>
          <input
            type="file"
            accept="image/*,application/pdf"
            onChange={(e) => setFormData({ ...formData, file: e.target.files[0] })}
            className="form-input"
            name="file"
          />
        </div>
        {error && <p className="error">{error}</p>}
        <button type="submit" className="submit-button">
          Add Transaction
        </button>
      </form>
    </div>
  );
}

export default TransactionForm;
