function Charts({ transactions }) {
  const [byCategoryData, setByCategoryData] = useState({});
  const [byDateData, setByDateData] = useState({});

  useEffect(() => {
    if (transactions && transactions.length > 0) {
      // Expenses by Category
      const categories = [...new Set(transactions.map(t => t.category))];
      const amounts = categories.map(cat =>
        transactions
          .filter(t => t.category === cat && t.type === 'expense')
          .reduce((sum, t) => sum + t.amount, 0)
      );
      setByCategoryData({
        labels: categories,
        datasets: [{
          label: 'Expenses by Category',
          data: amounts,
          backgroundColor: 'rgba(76, 81, 191, 0.6)',
        }]
      });

      // Expenses by Date
      const dates = [...new Set(transactions.map(t => formatDate(t.date)))];
      const dailyExpenses = dates.map(date =>
        transactions
          .filter(t => formatDate(t.date) === date && t.type === 'expense')
          .reduce((sum, t) => sum + t.amount, 0)
      );
      setByDateData({
        labels: dates,
        datasets: [{
          label: 'Expenses by Date',
          data: dailyExpenses,
          backgroundColor: 'rgba(99, 179, 237, 0.6)',
        }]
      });
    }
  }, [transactions]);

  return (
    <div className="card">
      <h2 className="form-title">Expense Analysis</h2>
      <div style={{ height: '400px', marginBottom: '50px' }}>
        <h4>By Category</h4>
        <Bar data={byCategoryData} options={{ responsive: true, maintainAspectRatio: false }} />
      </div>
      <div style={{ height: '400px' }}>
        <h4>By Date</h4>
        <Bar data={byDateData} options={{ responsive: true, maintainAspectRatio: false }} />
      </div>
    </div>
  );
}
