import { useState, useEffect } from 'react';
import { getTransactions } from '../services/transactionService';

function Dashboard() {
  const [transactions, setTransactions] = useState([]);
  const [summary, setSummary] = useState([]);

  useEffect(() => {
    const fetchData = async () => {
      try {
        const data = await getTransactions({ page: 1 }); // Remove date filters if unsupported
        setTransactions(data.transactions || []);
        setSummary(data.summary || []);
      } catch (error) {
        console.error('Error fetching transactions:', error);
      }
    };
    fetchData();
  }, []);

  // Prepare data for Expenses by Category (Bar Chart) with cap
  const expensesByCategory = transactions
    .filter(t => t.type === 'expense')
    .map(t => ({ ...t, amount: Math.min(Math.abs(t.amount), 10000) * (t.amount < 0 ? -1 : 1) })) // Cap and preserve sign
    .reduce((acc, t) => {
      acc[t.category] = (acc[t.category] || 0) + t.amount;
      return acc;
    }, {});
  const categoryLabels = Object.keys(expensesByCategory);
  const categoryData = Object.values(expensesByCategory);

  // Prepare data for Expenses by Date (Line Chart) with cap
  const expensesByDate = transactions
    .filter(t => t.type === 'expense')
    .map(t => ({ ...t, amount: Math.min(Math.abs(t.amount), 10000) * (t.amount < 0 ? -1 : 1) })) // Cap and preserve sign
    .reduce((acc, t) => {
      const date = new Date(t.date).toLocaleDateString();
      acc[date] = (acc[date] || 0) + t.amount;
      return acc;
    }, {});
  const dateLabels = Object.keys(expensesByDate).sort();
  const dateData = Object.values(expensesByDate);

  return (
    <div className="card">
      <h2 className="form-title">Dashboard</h2>
      <table>
        <thead>
          <tr>
            <th>Date</th>
            <th>Type</th>
            <th>Amount</th>
            <th>Category</th>
            <th>Description</th>
          </tr>
        </thead>
        <tbody>
          {transactions.map(t => (
            <tr key={t._id}>
              <td>{new Date(t.date).toLocaleDateString()}</td>
              <td>{t.type.charAt(0).toUpperCase() + t.type.slice(1)}</td>
              <td>{t.type === 'expense' ? `-${Math.min(Math.abs(t.amount), 10000)}` : t.amount}</td>
              <td>{t.category}</td>
              <td>{t.description}</td>
            </tr>
          ))}
        </tbody>
      </table>
      <h3>Monthly Summary</h3>
      {summary.length > 0 ? (
        summary.map(s => (
          <div key={s._id} className="summary-item">
            <p>
              Month: {s._id}, Income: ${s.totalIncome.toFixed(2)}, Expenses: ${Math.min(s.totalExpenses, 10000).toFixed(2)}, Savings: ${(s.totalIncome - Math.min(s.totalExpenses, 10000)).toFixed(2)}
            </p>
          </div>
        ))
      ) : (
        <p>No summary data available. Ensure backend provides summary or adjust endpoint.</p>
      )}
      <h3>Charts (Static Preview)</h3>
      <p>
        Charts cannot be rendered interactively due to canvas panel limitations. Below are static previews based on capped data (max 10,000). Copy to canvas panel if available.
      </p>
      <h4>Expenses by Category</h4>
      <p>
        Categories: {categoryLabels.join(', ')}<br />
        Amounts: {categoryData.join(', ')}<br />
        (Bar Chart: Use JSON below in canvas panel if supported)
      </p>
      <pre>
{`{
  "type": "bar",
  "data": {
    "labels": ${JSON.stringify(categoryLabels)},
    "datasets": [{
      "label": "Expenses",
      "data": ${JSON.stringify(categoryData)},
      "backgroundColor": ["#FF6384", "#36A2EB", "#FFCE56", "#4BC0C0", "#9966FF"],
      "borderColor": ["#FF6384", "#36A2EB", "#FFCE56", "#4BC0C0", "#9966FF"],
      "borderWidth": 1
    }]
  },
  "options": {
    "scales": {
      "y": {
        "beginAtZero": true,
        "title": {
          "display": true,
          "text": "Amount ($)"
        }
      }
    },
    "plugins": {
      "legend": {
        "position": "top"
      },
      "title": {
        "display": true,
        "text": "Expenses by Category"
      }
    }
  }
}`}
      </pre>
      <h4>Expenses by Date</h4>
      <p>
        Dates: {dateLabels.join(', ')}<br />
        Amounts: {dateData.join(', ')}<br />
        (Line Chart: Use JSON below in canvas panel if supported)
      </p>
      <pre>
{`{
  "type": "line",
  "data": {
    "labels": ${JSON.stringify(dateLabels)},
    "datasets": [{
      "label": "Expenses",
      "data": ${JSON.stringify(dateData)},
      "fill": false,
      "borderColor": "#FF6384",
      "tension": 0.1
    }]
  },
  "options": {
    "scales": {
      "y": {
        "beginAtZero": true,
        "title": {
          "display": true,
          "text": "Amount ($)"
        }
      },
      "x": {
        "title": {
          "display": true,
          "text": "Date"
        }
      }
    },
    "plugins": {
      "legend": {
        "position": "top"
      },
      "title": {
        "display": true,
        "text": "Expenses by Date"
      }
    }
  }
}`}
      </pre>
      <p>
        Note: If the canvas panel is not visible, check your Grok 3 UI settings. For inline charts, consider integrating react-chartjs-2 (requires `npm install react-chartjs-2 chart.js` and component setup).
      </p>
    </div>
  );
}

export default Dashboard;
