import axios from 'axios';

const api = axios.create({
  baseURL: process.env.REACT_APP_API_URL || 'http://localhost:5000/api',
  // withCredentials: true, // Uncomment if using cookies
});

// Function to set Authorization header with Bearer token
export const setAuthToken = (token) => {
  if (token) {
    console.log('api.js: Setting auth token:', token.substring(0, 10) + '...'); // Log partial token for security
    api.defaults.headers.common['Authorization'] = `Bearer ${token}`;
  } else {
    console.log('api.js: Removing auth token');
    delete api.defaults.headers.common['Authorization'];
  }
  console.log('api.js: Updated headers:', api.defaults.headers.common);
};

console.log('api.js: Initial headers:', api.defaults.headers.common);
export default api;
