import { useState } from 'react';
import { createTransaction } from '../services/transactionService';

function TransactionForm({ onAdd, onError }) {
  const [formData, setFormData] = useState({ type: 'expense', amount: '', category: '', date: '', description: '' });

  const handleChange = (e) => {
    setFormData({ ...formData, [e.target.name]: e.target.value });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      const transactionData = {
        ...formData,
        amount: parseFloat(formData.amount) || 0,
        date: new Date(formData.date).toISOString(),
      };
      await createTransaction(transactionData);
      setFormData({ type: 'expense', amount: '', category: '', date: '', description: '' });
      onAdd(); // Refresh the transaction list
    } catch (error) {
      console.error('TransactionForm.js: Error adding transaction:', error);
      onError('Failed to add transaction. Check console for details.');
    }
  };

  return (
    <form onSubmit={handleSubmit}>
      <select name="type" value={formData.type} onChange={handleChange}>
        <option value="expense">Expense</option>
        <option value="income">Income</option>
      </select>
      <input type="number" name="amount" value={formData.amount} onChange={handleChange} placeholder="Amount" required />
      <input type="text" name="category" value={formData.category} onChange={handleChange} placeholder="Category" required />
      <input type="date" name="date" value={formData.date} onChange={handleChange} required />
      <input type="text" name="description" value={formData.description} onChange={handleChange} placeholder="Description" />
      <button type="submit">Add Entry</button>
    </form>
  );
}

export default TransactionForm;
