import { useState, useEffect } from 'react';
import { getTransactions } from '../services/transactionService';

function Dashboard() {
  const [transactions, setTransactions] = useState([]);
  const [summary, setSummary] = useState([]);

  useEffect(() => {
    const fetchData = async () => {
      const data = await getTransactions({ page: 1 });
      setTransactions(data.transactions);
      setSummary(data.summary || []);
    };
    fetchData();
  }, []);

  return (
    <div className="card">
      <h2 className="form-title">Dashboard</h2>
      <table>
        <thead>
          <tr>
            <th>Date</th>
            <th>Type</th>
            <th>Amount</th>
            <th>Category</th>
            <th>Description</th>
          </tr>
        </thead>
        <tbody>
          {transactions.map(t => (
            <tr key={t._id}>
              <td>{new Date(t.date).toLocaleDateString()}</td>
              <td>{t.type.charAt(0).toUpperCase() + t.type.slice(1)}</td>
              <td>{t.type === 'expense' ? `-${t.amount.toFixed(2)}` : t.amount.toFixed(2)}</td>
              <td>{t.category}</td>
              <td>{t.description}</td>
            </tr>
          ))}
        </tbody>
      </table>
      <h3>Monthly Summary</h3>
      {summary.length > 0 ? (
        summary.map(s => (
          <div key={s._id} className="summary-item">
            <p>
              Month: {s._id}, 
              Income: ${s.totalIncome.toFixed(2)}, 
              Expenses: ${s.totalExpenses.toFixed(2)}, 
              Savings: ${(s.savings).toFixed(2)}
            </p>
          </div>
        ))
      ) : (
        <p>No summary data available.</p>
      )}
    </div>
  );
}

export default Dashboard;
